import React from 'react';
import { API_IMG } from './api/api-key';






const MovieBox = ({title, poster_path, release_date, vote_average, overview}) =>{
    return(
        <div className='card-container'>
            <div className='card-body'>
          <img src={API_IMG + poster_path } alt={title} className='card-img'></img>
            <div className='card-body'>     
                <h3>{title}</h3>
                <h4>ImDb: {vote_average}</h4>
                <h5>Release Date: {release_date}</h5>
                <br></br>
                <h6>Overview</h6>
                <p>{overview}</p>  
               <button type='button' className='btn'>View More</button>
                                  
            </div>
          </div> 
        </div> 

    )
}
export default MovieBox;
