import React, { useEffect, useState } from 'react'
import { API_SEARCH, API_URL } from './api/api-key'
import './App.css';

function App() {

const [movies, setMovies] = useState([])
const [searchTerm, setSearchTerm] = useState('')


useEffect(() => {
    fetch(API_URL)
    .then(response => response.json())
    .then(data => {
        setMovies(data.results)
    })

},[])

//Below here we have started to apply the search component logic in order to be able to search term//

const handleOnSubmit = (e) => {
    e.preventDefault()

    if (searchTerm) {


    fetch(API_SEARCH + searchTerm)
    .then(resp => resp.json())
    .then(data => {
        setSearchTerm(data.results)
    })

    setSearchTerm('');

      }
};

const handleOnChange = (e) => {

        (e.target.value)



}
    



  return (
<>
<header>
    <form onSubmit={handleOnSubmit}>
        <input type='search' 
               placeholder='search'
               value={setTerm}
               onChange={handleOnChange}> 
               </input>
        </form>
</header>

    <div movie-container>
        {movies.map((movie) =>(
             <movie key={movie.id} {...movie} />
        ))}
    </div>

    </>
  )
}

export default App
