import React from 'react';
import { API_IMG } from './api/api-key';




const MovieBox = ({title, poster_path, release_date, vote_average, overview}) =>{
    return(
        <div className='card-container'>
            <h1>{title}</h1>
          <img src={API_IMG + poster_path } alt={title} className='movie-img'></img>
           <p className='movie-overview'>{overview}</p>
           <span className='date'>{release_date}</span>
           <h4 className='vote-ave'>{vote_average}</h4> 
        </div> 

    )
}
export default MovieBox;
