import React, { useEffect, useState } from 'react';
import { API_URL} from './api/api-key';
import MovieBox from './MovieBox';
import './App.css';


function App() {

const [movies, setMovies ] = useState([])



useEffect(() =>{
   fetch(API_URL)
   .then(resp => resp.json())
   .then(data =>{
      console.log(data)
      setMovies(data.results)
   })

},[])






  return (

    <>

    <header>
      <form>
        <input type='search' 
         placeholder='search'
         className='search'

         ></input>
      </form>  
    </header> 


    <div className="container">
      <div className='grid'>
      {movies?.map((movie)=>
     <MovieBox key={movie.id} {...movie}/>)}
      </div>  
    </div>

    </>
  );
}

export default App;
