import React, { useEffect, useState } from 'react'
import { API_SEARCH, API_URL} from './api/api-key'
import './App.css';
import MovieBox from './MovieBox';


function App() {

    const [movies, setMovies] = useState([]);
    const [searchTerm, setSearchTerm ] = useState('');
    
    
    
    useEffect(() => {
    
        fetch(API_URL)
        .then(resp => resp.json())
        .then(data =>{
            setMovies(data.results)
        })
    
    },[])
    
    
    const handleOnSubmit = (e) => {
    
                   e.preventDefault()
                   
        if (searchTerm)	{	   
                   
                fetch(API_SEARCH + searchTerm)
                .then(resp => resp.json())
                .then(data => setSearchTerm(data.results))  
                      
    
    
        setSearchTerm('');
    
           }
        
       };
    
    
    const handleOnChange = (e)  => {
    
         setSearchTerm(e.target.value)
    
    }
    
    
     return(
<>

<header>
     <form OnSubmit={handleOnSubmit}>
	    <input className='search'
	    type='search'
	    placeholder='search'
	    value={searchTerm}
	    onChange={handleOnChange}></input>
	 </form> 
</header>




    <div movie-container>
        {movies.map((movie) =>(
             <MovieBox key={movie.id} {...movie} />
        ))}
    </div>

    <footer id='footer'>
    <div className='social-container'>
      <ul>
         <li>
    <a href='http://'><i class='fab fa-facebook-square'></i>facebook</a>
         </li>
         <li>
    <a href='http://'><i class='fab fa-instagram-square'></i>instagram</a>
         </li>
         <li>
    <a href='http://'><i class='fab fa-linkedin-square'></i>youtube</a>
         </li>    
      </ul>  
    </div>
  </footer>

    </>
  )
}

export default App
